import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Home() {
  const [usuarios, setUsuarios] = useState([]);
  const [usuarioSelecionado, setUsuarioSelecionado] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:3001/users')
      .then(res => setUsuarios(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Usuários</h1>
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>ID</th><th>Nome</th><th>Email</th>
          </tr>
        </thead>
        <tbody>
          {usuarios.map(u => (
            <tr key={u.id} onClick={() => setUsuarioSelecionado(u)} style={{ cursor: 'pointer' }}>
              <td>{u.id}</td>
              <td>{u.nome}</td>
              <td>{u.email}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {usuarioSelecionado && (
        <div style={{ marginTop: '2rem' }}>
          <h2>Detalhes do Usuário</h2>
          <p>ID: {usuarioSelecionado.id}</p>
          <p>Nome: {usuarioSelecionado.nome}</p>
          <p>Email: {usuarioSelecionado.email}</p>
          <button onClick={() => setUsuarioSelecionado(null)}>Fechar</button>
        </div>
      )}
    </div>
  );
}

export default Home;
